                Pll (vrf,v_lo,vph_det,vdetect);

   analog begin
      V(demod_gnd) <+ 0.0;

      V(vrf) <+ rf_gain * V(vin);

      V(vout) <+ af_gain*V(vaf) + af_lev_shift;
   end
endmodule
