parameter real LU0		= 0.0;
parameter real WU0		= 0.0;
parameter real PU0		= 0.0;

parameter real ETAMOB		= 1.0;				// Effective field parameter (should be kept close to 1)

parameter real UA		= 0.001;			// Mobility reduction coefficient
