       	 thyr_state = `ON;
      end else if( (I(vanode,vcathode) < ihold) && (thyr_state == `ON)
          && (I(vgate,vcathode) < iturn_on) )begin
         thyr_state = `OFF;
      end

      if (thyr_state == `OFF) begin
      	 I(vanode,vcathode) <+ 0.0;
      end
      else begin
      	 V(vanode,vcathode) <+ von ;
